  async loadAllSettingsFilesAsync({
    forceReload = false,
  }: { forceReload?: boolean } = {}): Promise<Array<CfgTmplFileInfo>> {
    if (
      !forceReload &&
      this.configurationFiles &&
      this.configurationFiles.length > 0
    )
      return this.configurationFiles;

    //console.log(
    //  `this.CONFIGURATION_BURST_FOLDER_PATH = ${this.CONFIGURATION_BURST_FOLDER_PATH}`
    //);

    const burstConfigFilePaths = await this.unixCliService.findAsync(
      this.CONFIGURATION_BURST_FOLDER_PATH,
      {
        matching: ['*.xml'],
      },
    );

    //console.log(
    //  `burstConfigFilePaths1 = ${JSON.stringify(burstConfigFilePaths)}`
    //);

    const reportsConfigFilePaths = await this.unixCliService.findAsync(
      this.CONFIGURATION_REPORTS_FOLDER_PATH,
      {
        matching: ['settings.xml'],
        recursive: true,
      },
    );

    //console.log(
    //  `reportsConfigFilePaths2 = ${JSON.stringify(reportsConfigFilePaths)}`
    //);

    const samplesConfigFilePaths = await this.unixCliService.findAsync(
      this.CONFIGURATION_SAMPLES_FOLDER_PATH,
      {
        matching: ['settings.xml'],
        recursive: true,
      },
    );

    // console.log(
    //   `samplesConfigFilePaths3 = ${JSON.stringify(samplesConfigFilePaths)}`
    // );

    const configFilePaths = burstConfigFilePaths
      .concat(reportsConfigFilePaths)
      .concat(samplesConfigFilePaths);

    const configurationFiles: Array<CfgTmplFileInfo> = [];

    for (let filePath of configFilePaths) {
      const configurationFilePath = filePath;
      const configurationFileName = Utilities.basename(configurationFilePath);

      const isFallbackSettings =
        configurationFilePath.endsWith('burst/settings.xml');

      const isNotSettings =
        configurationFilePath.endsWith('_defaults/settings.xml') ||
        configurationFilePath.endsWith('preferences/settings.xml');

      if (isNotSettings) continue;

      //console.log(
      //  `configurationFilePath4 = ${JSON.stringify(configurationFilePath)}`
      // );

      const settingsFileContent = await this.unixCliService.catAsync(filePath);

      let startPos =
        settingsFileContent.indexOf('<template>') + '<template>'.length;
      let endPos = settingsFileContent.indexOf('</template>');
      const settingsTemplateName = settingsFileContent
        .substring(startPos, endPos)
        .trim();

      startPos =
        settingsFileContent.indexOf('<reportdistribution>') +
        '<reportdistribution>'.length;
      endPos = settingsFileContent.indexOf('</reportdistribution>');
      const boolReportDistribution = Boolean(
        JSON.parse(settingsFileContent.substring(startPos, endPos).trim()),
      );

      startPos =
        settingsFileContent.indexOf('<reportgenerationmailmerge>') +
        '<reportgenerationmailmerge>'.length;
      endPos = settingsFileContent.indexOf('</reportgenerationmailmerge>');
      const boolReportGenerationMailMerge = Boolean(
        JSON.parse(settingsFileContent.substring(startPos, endPos).trim()),
      );

      startPos =
        settingsFileContent.indexOf('<visibility>') + '<visibility>'.length;
      endPos = settingsFileContent.indexOf('</visibility>');
      const strVisibility = settingsFileContent
        .substring(startPos, endPos)
        .trim();

      startPos = settingsFileContent.indexOf('<useconn>') + '<useconn>'.length;
      endPos = settingsFileContent.indexOf('</useconn>');
      const boolUseEmailConnection = Boolean(
        JSON.parse(settingsFileContent.substring(startPos, endPos).trim()),
      );

      startPos =
        settingsFileContent.indexOf('<conncode>') + '<conncode>'.length;
      endPos = settingsFileContent.indexOf('</conncode>');
      const strEmailConnectionCode = settingsFileContent
        .substring(startPos, endPos)
        .trim();

      let templateRelativeFilePath = `./config/burst/${configurationFileName}`;
      let typeOfConfiguration = 'config-burst-legacy';

      const folderName = Utilities.basename(
        Utilities.dirname(configurationFilePath),
      );

      let dsInputType = '';

      if (configurationFilePath.includes(`config/reports/${folderName}`)) {
        typeOfConfiguration = 'config-reports';
        templateRelativeFilePath = `./config/reports/${folderName}/settings.xml`;

        if (boolReportGenerationMailMerge) {
          const reportingXmlFilePath = `${Utilities.dirname(
            configurationFilePath,
          )}/reporting.xml`;

          const reportingXmlFileContent =
            await this.unixCliService.catAsync(reportingXmlFilePath);
          startPos =
            reportingXmlFileContent.indexOf('<type>') + '<type>'.length;
          endPos = reportingXmlFileContent.indexOf('</type>');
          dsInputType = reportingXmlFileContent
            .substring(startPos, endPos)
            .trim();
        }
      } else if (
        configurationFilePath.includes(`config/samples/${folderName}`)
      ) {
        typeOfConfiguration = 'config-samples';
        templateRelativeFilePath = `./config/samples/${folderName}/settings.xml`;
      }

      configurationFiles.push({
        fileName: configurationFileName,
        filePath: Utilities.slash(configurationFilePath),
        relativeFilePath: templateRelativeFilePath,
        templateName: settingsTemplateName,
        isFallback: isFallbackSettings,
        capReportDistribution: boolReportDistribution,
        capReportGenerationMailMerge: boolReportGenerationMailMerge,
        dsInputType: dsInputType,
        visibility: strVisibility,
        notes: '',
        folderName: folderName,
        type: typeOfConfiguration,
        activeClicked: false,
        useEmlConn: boolUseEmailConnection,
        emlConnCode: strEmailConnectionCode,
      });
    }

    this.configurationFiles = configurationFiles;

    // console.log(
    //   `this.configurationFiles = ${JSON.stringify(this.configurationFiles)}`
    // );
    return configurationFiles;
  }

  async loadAllConnectionFilesAsync() {
    if (this.connectionsLoading == 1) return;

    this.connectionsLoading = 1;

    this.defaultEmailConnectionFile = undefined;

    const connectionFiles: Array<ExtConnection> = [];

    const connectionFilePaths = await this.unixCliService.findAsync(
      this.CONFIGURATION_CONNECTIONS_FOLDER_PATH,
      {
        matching: ['*.xml'],
      },
    );

    for (let connectionFilePath of connectionFilePaths) {
      let connectionSettings = {
        documentburster: {
          connection: null,
        },
      };

      // console.log(`connectionFilePath = ${JSON.stringify(connectionFilePath)}`);

      connectionSettings.documentburster = await this.apiService.get(
        '/cfgman/rb/load-connection',
        {
          path: connectionFilePath,
        },
      );
      const connXml = connectionSettings.documentburster.connection;

      //console.log(`connXml = ${JSON.stringify(connXml)}`);

      const connectionFileName = Utilities.basename(connectionFilePath);
      //console.log(
      //  `this.configurationFiles = ${JSON.stringify(this.configurationFiles)}`
      //);
      connectionFiles.push({
        fileName: connectionFileName,
        filePath: connectionFilePath,
        connectionCode: connXml.code,
        connectionName: connXml.name,
        connectionType: connectionFileName.startsWith('eml-')
          ? 'email-connection'
          : 'database-connection',
        activeClicked: false,
        defaultConnection: connXml.defaultConnection,
        usedBy: this.configurationFiles
          .filter((conf) => conf.useEmlConn && conf.emlConnCode == connXml.code)
          .map((conf) => conf.templateName)
          .join(', '),
        emailserver: {
          host: connXml.emailserver.host,
          port: connXml.emailserver.port,
          userid: connXml.emailserver.userid,
          userpassword: connXml.emailserver.userpassword,
          usessl: connXml.emailserver.usessl,
          usetls: connXml.emailserver.usetls,
          fromaddress: connXml.emailserver.fromaddress,
          name: connXml.emailserver.name,
        },
      });
    }

    this.connectionFiles = connectionFiles;

    this.defaultEmailConnectionFile = this.getConnectionDetails({
      connectionType: 'email-connection',
      defaultConnection: true,
      connectionCode: '',
    });

    //console.log(
    //  `this.defaultEmailConnectionFile = ${JSON.stringify(
    //    this.defaultEmailConnectionFile
    //  )}`
    //);

    this.connectionsLoading = 0;
  }

  isDefaultEmailConnectionConfigured(): boolean {
    return (
      this.defaultEmailConnectionFile &&
      this.defaultEmailConnectionFile.emailserver &&
      this.defaultEmailConnectionFile.emailserver.host != 'Email Server Host' &&
      this.defaultEmailConnectionFile.emailserver.name != 'From Name' &&
      this.defaultEmailConnectionFile.emailserver.fromaddress !=
        'from@emailaddress.com' &&
      this.defaultEmailConnectionFile.emailserver.userid !=
        'From Email User ID' &&
      this.defaultEmailConnectionFile.emailserver.userpassword !=
        'From Email Password'
    );
  }

  async loadAllReportTemplatesFilesAsync() {
    let relativeFilePath: string, tplType: string;
    const emailsTemplateFiles: Array<TmplFileInfo> = [];
    const reportsTemplateFiles: Array<TmplFileInfo> = [];

    let reportsTemplateFilePaths = await this.unixCliService.findAsync(
      `${this.CONFIGURATION_TEMPLATES_FOLDER_PATH}/reports`,
      {
        matching: ['*.docx', '*.html'],
        files: true,
        directories: false,
        recursive: true,
      },
    );

    //console.log(
    //  `reportsTemplateFilePaths = ${JSON.stringify(reportsTemplateFilePaths)}`
    //);

    //const samplesReportsTemplateFilePaths = [];

    //reportsTemplateFilePaths = reportsTemplateFilePaths.concat(
    //  samplesReportsTemplateFilePaths
    //);

    for (let filePath of reportsTemplateFilePaths) {
      console.log(`filePath = ${filePath}`);

      const reportTemplateFileName = Utilities.basename(filePath);

      const folderName = Utilities.basename(Utilities.dirname(filePath));

      if (filePath.includes('samples/reports')) {
        tplType = 'template-report-sample';
        relativeFilePath = filePath.replace(
          `${this.PORTABLE_EXECUTABLE_DIR}/templates/samples/reports/`,
          '',
        );
      } else {
        tplType = 'template-report';
        relativeFilePath = filePath.replace(
          `${this.PORTABLE_EXECUTABLE_DIR}/templates/reports/`,
          '',
        );
      }

      reportsTemplateFiles.push({
        fileName: reportTemplateFileName,
        filePath: filePath,
        type: tplType,
        folderName: folderName,
        relativeFilePath: relativeFilePath,
      });
    }

    const templateFiles = emailsTemplateFiles.concat(reportsTemplateFiles);

    this.templateFiles = templateFiles;
    return templateFiles;
  }

  async saveReportingFileAsync(
    filePath: string,
    xmlReporting: { documentburster: {} },
  ) {
    console.log(
      `saveReportingFileAsynce xmlReporting = ${JSON.stringify(xmlReporting)}`,
    );
    return this.apiService.post(
      `/cfgman/rb/save-reporting?path=${filePath}`,
      xmlReporting.documentburster,
    );
  }

  async loadReportingFileAsync(filePath: string): Promise<any> {
    return this.apiService.get('/cfgman/rb/load-reporting', {
      path: filePath,
    });
  }

  getConfigurations(visibility?: string) {
    return this.configurationFiles.filter((configuration) => {
      let filterCondition = configuration.type != 'config-samples';

      if (visibility)
        filterCondition =
          filterCondition && configuration.visibility === visibility;

      return filterCondition;
    });
  }

  getMailMergeConfigurations(visibility?: string) {
    return this.configurationFiles.filter((configuration) => {
      let filterCondition = configuration.capReportGenerationMailMerge;

      if (visibility)
        filterCondition =
          filterCondition && configuration.visibility === visibility;

      return filterCondition;
    });
  }

  getReportTemplates(outputType: string, filter: { samples: boolean }) {
    return this.templateFiles.filter((template) => {
      let filterConditionDocx = false;
      let filterConditionHtml = false;

      if (outputType == 'output.docx') {
        if (filter.samples)
          filterConditionDocx = template.fileName.endsWith('.docx');
        else
          filterConditionDocx =
            template.fileName.endsWith('.docx') &&
            !template.type.includes('-sample');
      }

      if (outputType == 'output.html') {
        if (filter.samples)
          filterConditionHtml = template.fileName.endsWith('.html');
        else
          filterConditionHtml =
            template.fileName.endsWith('.html') &&
            !template.type.includes('-sample');
      }

      return filterConditionDocx || filterConditionHtml;
    });
  }

  getSampleConfigurations(visibility?: string) {
    return this.configurationFiles.filter((configuration) => {
      let filterCondition = configuration.type == 'config-samples';

      if (visibility)
        filterCondition =
          filterCondition && configuration.visibility === visibility;

      return filterCondition;
    });
  }

  getConnectionDetails({
    connectionType,
    defaultConnection,
    connectionCode,
  }: {
    connectionType: string;
    defaultConnection: boolean;
    connectionCode: string;
  }) {
    //console.log(
    //  `this.connectionFiles = ${JSON.stringify(this.connectionFiles)}`
    //);
    let connFiles = [];

    if (this.connectionFiles.length > 0) {
      connFiles = this.connectionFiles.filter((connection: ExtConnection) => {
        return connection.connectionType == connectionType;
      });

      if (defaultConnection) {
        connFiles = connFiles.filter((connection: ExtConnection) => {
          return connection.defaultConnection;
        });
      }

      if (connectionCode && connectionCode.length > 0) {
        connFiles = connFiles.filter((connection: ExtConnection) => {
          return connection.connectionCode == connectionCode;
        });
      }

      if (connFiles && connFiles.length == 1) {
        return connFiles[0];
      }
    }
    return undefined;
  }
}

import { Component, OnInit } from '@angular/core';

import { extraPackagesTemplate } from './extra-packages.template';

import { BashService } from '../bash.service';
import { ConfirmService } from '../../../components/dialog-confirm/confirm.service';
import { ExecutionStatsService } from '../../../providers/execution-stats.service';
import Utilities from '../../../helpers/utilities';
import { TranslateService } from '@ngx-translate/core';
import UtilitiesElectron from '../../../helpers/utilities-electron';
import { ElectronService } from '../../../core/services';

interface ExtPackage {
  id: string;
  name: string;
  icon: string;
  website: string;
  description: string;
  status: string;
  packageManager: string;
  cmdInstall: string;
  cmdUnInstall: string;
  cmdGetInfo: string;
}

@Component({
  selector: 'dburst-extra-packages',
  template: ` ${extraPackagesTemplate} `,
})
export class ExtraPackagesComponent implements OnInit {
  protected extraPackages = [
    {
      id: 'notepadplusplus',
      name: 'Notepad++',
      icon: 'notepad++.svg',
      website: 'https://notepad-plus-plus.org',
      description: ` is a free (as in free speech and also as in free beer) source code editor and Notepad replacement. Notepad++ is useful when editing ReportBurster configuration files and scripts.`,
      status: 'not-installed',
      packageManager: 'choco',
      cmdInstall: 'choco install notepadplusplus --yes',
      cmdUnInstall: 'choco uninstall notepadplusplus',
      cmdGetInfo: 'choco info notepadplusplus -lo',
    },
    {
      id: 'vscode',
      name: 'Visual Studio Code',
      icon: 'vscode-logo.svg',
      website: 'https://code.visualstudio.com',
      description: ` Code editing. Redefined.`,
      status: 'not-installed',
      packageManager: 'choco',
      cmdInstall: 'choco install vscode --yes',
      cmdUnInstall: 'choco uninstall vscode',
      cmdGetInfo: 'choco info vscode -lo',
    },
    {
      id: 'winmerge',
      name: 'WinMerge',
      icon: 'winmerge.png',
      website: 'https://winmerge.org',
      description: ` is an Open Source differencing and merging tool for Windows. WinMerge can compare both folders and files, presenting differences in a visual text format that is easy to understand and handle. WinMerge can be used for comparing ReportBurster configuration files and scripts.`,
      status: 'not-installed',
      packageManager: 'choco',
      cmdInstall: 'choco install winmerge --yes',
      cmdUnInstall: 'choco uninstall winmerge',
      cmdGetInfo: 'choco info winmerge -lo',
    },
    {
      id: 'docker-desktop',
      name: 'Docker Desktop',
      icon: 'docker.svg',
      website: 'https://www.docker.com/products/docker-desktop/',
      description: ` a very good containerization software. Docker is required for running other ReportBurster extra packages.`,
      status: 'not-installed',
      packageManager: 'choco',
      cmdInstall: 'choco install docker-desktop --yes',
      cmdUnInstall: 'choco uninstall docker-desktop',
      cmdGetInfo: 'choco info docker-desktop -lo',
    },
  ];

  constructor(
    protected translateService: TranslateService,
    protected bashService: BashService,
    protected electronService: ElectronService,
    protected executionStatsService: ExecutionStatsService,
    protected confirmService: ConfirmService,
  ) {}

  async ngOnInit() {
    await this.fetchExtraPackagesDetails();
  }

  async fetchExtraPackagesDetails() {
    for (const extraPackage of this.extraPackages) {
      const packageId = extraPackage.id;

      extraPackage.description = await this.translateService.instant(
        `AREAS.INSTALL-SETUP-UPGRADE.COMPONENTS.EXTRA-PACKAGES.INNER-HTML.${packageId.toUpperCase()}`,
      );

      //console.log(packageId);

      extraPackage.status = 'not-installed';

      const chocoInfoCommand = `choco info ${packageId} -lo`;
      //const chocoInfoCommand = `choco --version`;

      try {
        const { stdout, stderr } =
          await UtilitiesElectron.execNativeCommand(chocoInfoCommand);

        //console.log(`chocoInfo = ${chocoInfo}`);

        if (stdout && stdout.includes('1 packages installed.')) {
          extraPackage.status = 'installed';
        }
      } catch (error) {
        extraPackage.status = 'not-installed';
      }
    }
    // Sort the extraPackages array so that installed packages come first
    this.extraPackages.sort((a, b) => {
      if (a.status === 'installed' && b.status !== 'installed') {
        return -1;
      } else if (a.status !== 'installed' && b.status === 'installed') {
        return 1;
      } else {
        return 0;
      }
    });
  }

  doInstallUninstallAction(pckage: ExtPackage, action: string) {
    const dialogQuestion = `${Utilities.titleCase(action)} ${pckage.name}?`;

    this.confirmService.askConfirmation({
      message: dialogQuestion,
      confirmAction: () => {
        this.bashService.typeCommandOnTerminalAndThenPressEnter(
          `choco ${action} ${pckage.id} --yes`,
        );
      },
    });
  }
}

import { Injectable } from '@angular/core';
import { ApiService } from './api.service';
import Utilities from '../helpers/utilities';

export interface InspectResult {
  name: string;
  type: 'file' | 'dir' | 'symlink';
  size: number;
  absolutePath?: string;
  md5?: string;
  sha1?: string;
  sha256?: string;
  sha512?: string;
  mode?: number;
  accessTime?: Date;
  modifyTime?: Date;
  changeTime?: Date;
  birthTime?: Date;
}

@Injectable({
  providedIn: 'root',
})
export class FsService {
  constructor(protected apiService: ApiService) {}

  async removeAsync(path: string) {
    console.log('removeAsync', path);
    return this.apiService.delete(
      `/jobman/system/fs/delete-quietly?path=${encodeURIComponent(
        Utilities.slash(path),
      )}`,
    );
  }

  async copyAsync(
    sourcePath: string,
    destinationPath: string,
    criteria: {
      overwrite?:
        | boolean
        | ((src: any, dest: any) => boolean | Promise<boolean>);
      matching?: string[];
      ignoreCase?: boolean;
    } = {},
  ): Promise<void> {
    const overwrite =
      typeof criteria.overwrite === 'function'
        ? criteria.overwrite(sourcePath, destinationPath)
        : criteria.overwrite;

    console.log(`overwrite = ${overwrite}`);

    let postUrl = `/jobman/system/fs/copy?fromPath=${encodeURIComponent(
      Utilities.slash(sourcePath),
    )}&toPath=${encodeURIComponent(Utilities.slash(destinationPath))}`;

    if (overwrite) postUrl = `${postUrl}&overwrite=${overwrite}`;
    if (criteria.matching) postUrl = `${postUrl}&matching=${criteria.matching}`;
    if (criteria.matching)
      postUrl = `${postUrl}&ignoreCase=${criteria.ignoreCase}`;

    return this.apiService.post(postUrl);
  }

  async existsAsync(path: string): Promise<'dir' | 'file' | 'other' | false> {
    return this.apiService.get(
      '/jobman/system/fs/exists',
      {
        path: encodeURIComponent(Utilities.slash(path)),
      },
      new Headers({
        Accept: 'text/plain',
        'Content-Type': 'application/json',
      }),
    );
  }

  async dirAsync(
    path: string,
    criteria: {
      empty?: true;
      mode?: string;
    } = {},
  ): Promise<void> {
    return this.apiService.post(
      `/jobman/system/fs/dir?path=${encodeURIComponent(Utilities.slash(path))}`,
      criteria,
    );
  }

  async readAsync(path: string): Promise<string> {
    return this.apiService.get(
      '/jobman/system/fs/read-file-to-string',
      { path: encodeURIComponent(path) },
      new Headers({
        Accept: 'text/plain',
        'Content-Type': 'application/json',
      }),
    );
  }

  async writeAsync(path: string, content: string) {
    const encodedPath = encodeURIComponent(Utilities.slash(path));
    return this.apiService.post(
      `/jobman/system/fs/write-string-to-file?path=${encodedPath}`,
      content,
      new Headers({
        'Content-Type': 'text/plain',
      }),
    );
  }

  async fileAsync(
    path: string,
    criteria: {
      content?: string | Buffer | Object | Array<any>;
      jsonIndent?: number;
      mode?: number | string;
    } = {},
  ): Promise<void> {
    const encodedPath = encodeURIComponent(Utilities.slash(path));

    return this.apiService.post(
      `/jobman/system/fs/file?path=${encodedPath}`,
      criteria,
    );
  }

  inspectAsync(
    path: string,
    criteria: {
      checksum?: 'md5' | 'sha1' | 'sha256' | 'sha512';
      mode?: boolean;
      times?: boolean;
      absolutePath?: boolean;
      symlinks?: 'report' | 'follow';
    } = {},
  ): Promise<InspectResult | undefined> {
    return this.apiService.get(
      `/jobman/system/fs/inspect/${encodeURIComponent(Utilities.slash(path))}`,
      {
        checksum: criteria.checksum,
        mode: criteria.mode,
        times: criteria.times,
        absolutePath: criteria.absolutePath,
        symlinks: criteria.symlinks,
      },
    );
  }

  async resolveAsync(path: string): Promise<string> {
    console.log(`path = ${path}`);
    return this.apiService.get(
      '/jobman/system/fs/resolve',
      {
        path: encodeURIComponent(Utilities.slash(path)),
      },
      new Headers({
        Accept: 'text/plain',
        'Content-Type': 'application/json',
      }),
    );
  }
}

import { Injectable } from '@angular/core';
import Utilities from '../helpers/utilities';
import { ApiService } from './api.service';
import { ToastrMessagesService } from './toastr-messages.service';
import { SettingsService } from './settings.service';
import { FsService } from './fs.service';

interface ProcessOutputResult {
  success: boolean;
  outputLines: string[];
}

@Injectable({
  providedIn: 'root',
})
export class ShellService {
  logFilePath: string;
  isJavaOk = true;

  constructor(
    protected settingsService: SettingsService,
    protected messagesService: ToastrMessagesService,
    protected fsService: FsService,
    protected apiService: ApiService,
  ) {}

  async checkJavaVersion() {
    this.isJavaOk = this.settingsService.isJServerStarted;
  }

  async doKillOldExeThenCopyAndStartNewExe(
    jobFilePath: string,
    newExeFilePath: string,
    upgDbTempFolderPath: string,
  ) {
    const jblFilePath = Utilities.slash(
      await this.apiService.get('/jobman/system/fs/resolve', {
        paths: jobFilePath,
      }),
    );

    const script = await this.apiService.post(
      '/jobman/system/child-process/spawn',
      {
        command: 'rb_rust_updater',
        args: ['--job_file_path', jblFilePath, '2>&1', '>>', this.logFilePath],
        options: {
          cwd: this.settingsService.PORTABLE_EXECUTABLE_DIR,
          shell: true,
        },
      },
    );

    script.data.stderr.on('data', (data) => {
      this.messagesService.showError(data);
    });
  }

  async runBatFile(
    arrguments: string[],
    workItemName?: string,
    exitCallback?: Function,
  ) {
    let message = 'Working ... Please wait.';
    if (workItemName) {
      message = 'Working on ' + workItemName + '. Please wait.';
    }
    this.messagesService.showInfo(message, '', {
      messageClass: 'java-started',
    });

    const commands = ['documentburster.bat'].concat(arrguments);
    const batScript = await this.apiService.post(
      '/jobman/system/child-process/spawn',
      commands,
    );

    console.log(`batScript = ${JSON.stringify(batScript)}`);
    this.handleScriptEvents(batScript, workItemName, exitCallback);
  }

  async generateMergeFileInTempFolder(filePaths: string[]): Promise<string> {
    console.log(`generateMergeFileInTempFolder filePaths = ${filePaths}`);
    const dir = `${this.settingsService.PORTABLE_EXECUTABLE_DIR}/temp/`;
    const uniqueId =
      Date.now().toString(36) + Math.random().toString(36).substring(2);
    const mergeFilePath = `${dir}merge-files-${uniqueId}`;

    const content = filePaths.join('\n');

    await this.fsService.writeAsync(mergeFilePath, content);
    return mergeFilePath;
  }

  async startStopTestEmailServer(command: string) {
    const message = 'Working ... Please wait.';

    this.messagesService.showInfo(message, '', {
      messageClass: 'java-started',
    });

    const currentWorkingDirPath = `${this.settingsService.PORTABLE_EXECUTABLE_DIR}/tools/test-email-server`;

    const batFile = command + 'TestEmailServer.bat';

    const batScript = await this.apiService.post(
      `/jobman/system/child-process/spawn?cwdPath=${encodeURIComponent(
        Utilities.slash(currentWorkingDirPath),
      )}`,
      [batFile],
    );

    this.handleScriptEvents(batScript, null, null);
  }

  async clearResumeJob(jobFilePath: string) {
    await this.fsService.removeAsync(jobFilePath);
    this.messagesService.showInfo('Job was cleared.');
  }

  handleScriptEvents(
    result: ProcessOutputResult,
    workItemName?: string,
    exitCallback?: Function,
  ) {
    // Handle the result here. This will be called when the process has finished executing.
    let message = 'Done';
    if (workItemName) {
      message = 'Done ' + workItemName;
    }
    if (result.success) {
      this.messagesService.showInfo(message, '', {
        messageClass: 'java-exited',
      });
    } else {
      this.messagesService.showError('Error', '', {
        messageClass: 'java-exited',
      });
    }
    if (exitCallback) {
      exitCallback();
    }
  }
}

