import { Injectable } from '@angular/core';
import { TranslateService } from '@ngx-translate/core';
import { BsModalRef, BsModalService } from 'ngx-bootstrap/modal';
import { SettingsService } from '../../providers/settings.service';
import { ToastrMessagesService } from '../../providers/toastr-messages.service';
import { AskForFeatureDialogComponent } from './ask-for-feature-dialog.component';
import { ShellService } from '../../providers/shell.service';
@Injectable()
export class AskForFeatureService {
  alreadyImplementedFeatures = [
    'ds.csvfile',
    'output.none',
    'output.docx',
    'output.html',
  ];

  messageTemplate = {
    to: 'support@reportburster.com',
    subject: "New Feature Request - '{{nameYourFeatureHere}}'",
    message: `Hello,
    
My name is {{YourNameHere}} and I work for {{YourCompanyNameHere}} where I am {{YourTitleHere}}.
    
We would be interested to get the new feature '{{nameYourFeatureHere}}' implemented faster in DocumentBurster.

Functional Requirements 

{{describeYourFunctionalRequirementsAsClearyAsPossibleHere}}

Non-Functional and Performance Related Requirements 

{{describeYourExpectedVolumeHere}}

{{describeYourOtherNonFunctionalRequirementsHere}}
    
Timeline

Ideally, we would need this new feature to be delivered no later than 

{{YourTimelineHere}}

Please let us know if it would be possible to get this feature implemented in DocumentBurster.

Sincerely,
{{YourNameHere}}
{{YourTitleHere}},{{YourCompanyNameHere}}
`,
  };

  modalRef?: BsModalRef;
  constructor(
    protected modalService: BsModalService,
    protected translateService: TranslateService,
    protected messagesService: ToastrMessagesService,
    protected settingsService: SettingsService,
    protected shellService: ShellService,
  ) {}

  showAskForFeature(options: any): Promise<any> {
    const titleLabel = this.translateService.instant(
      'COMPONENTS.ASK-FOR-FEATURE-DIALOG.TITLE',
    );

    const confirmLabel = this.translateService.instant(
      'COMPONENTS.ASK-FOR-FEATURE-DIALOG.CONFIRM',
    );

    return new Promise((resolve, reject) => {
      this.modalRef = this.modalService.show(AskForFeatureDialogComponent, {
        class: 'modal-lg',
      });

      this.modalRef.content.msgTo = this.messageTemplate.to;

      let requestedFeatureFriendly = '';

      const requestedFeature = options.requestedFeature;
      switch (requestedFeature) {
        case 'ds.tsvfile':
          requestedFeatureFriendly = 'TSV File (DataSource)';
          break;
        case 'ds.fixedwidthfile':
          requestedFeatureFriendly = 'Fixed-Width File (DataSource)';
          break;
        case 'ds.excelfile':
          requestedFeatureFriendly = 'Excel File (DataSource)';
          break;
        case 'ds.gsheet':
          requestedFeatureFriendly =
            'Google Sheets (cloud/saas service) (DataSource)';
          break;
        case 'ds.o365sheet':
          requestedFeatureFriendly =
            'Microsoft Office365 Excel (cloud/saas service) (DataSource)';
          break;
        case 'ds.database':
          requestedFeatureFriendly = 'Database (remote server) (DataSource)';
          break;
        case 'output.pdf':
          requestedFeatureFriendly = 'PDF Files (Output Type)';
          break;
        case 'output.xlsx':
          requestedFeatureFriendly = 'Excel (xlsx) Files (Output Type)';
          break;
        case 'output.html':
          requestedFeatureFriendly = 'HTML Files (Output Type)';
          break;
        default:
          requestedFeatureFriendly = '';
      }

      if (requestedFeatureFriendly) {
        this.modalRef.content.msgSubject = this.messageTemplate.subject.replace(
          '{{nameYourFeatureHere}}',
          requestedFeatureFriendly,
        );
        this.modalRef.content.msgMessage = this.messageTemplate.message.replace(
          '{{nameYourFeatureHere}}',
          requestedFeatureFriendly,
        );
      } else {
        this.modalRef.content.msgSubject = this.messageTemplate.subject;
        this.modalRef.content.msgMessage = this.messageTemplate.message;
      }

      this.modalRef.content.title = options.title ? options.title : titleLabel;
      this.modalRef.content.confirmLabel = options.confirmLabel
        ? options.confirmLabel
        : confirmLabel;

      this.modalRef.content.onClose.subscribe((result: boolean) => {
        resolve(result);
      });
    });
  }
}

import { Injectable } from '@angular/core';
import { TerminalService } from 'primeng/terminal';

import { ApiService } from '../../providers/api.service';
import Utilities from '../../helpers/utilities';
import { SettingsService } from '../../providers/settings.service';
import { FsService } from '../../providers/fs.service';
import UtilitiesElectron from '../../helpers/utilities-electron';

@Injectable({
  providedIn: 'root',
})
export class BashService {
  logFilePath = `${this.settingService.PORTABLE_EXECUTABLE_DIR}/logs/bash.service.log`;

  pTerminalInput: HTMLInputElement;

  constructor(
    protected apiService: ApiService,
    protected fsService: FsService,
    protected settingService: SettingsService,
    public terminalService: TerminalService,
  ) {}

  typeCommandOnTerminalAndThenPressEnter(command: string) {
    this.pTerminalInput.value = command;
    this.pTerminalInput.dispatchEvent(new Event('input'));
    this.pTerminalInput.dispatchEvent(
      new KeyboardEvent('keydown', {
        keyCode: 13,
      } as KeyboardEventInit),
    );
    this.pTerminalInput.focus();
  }

  async createJobFile(jobType: string): Promise<string> {
    let filePath = '';
    if (jobType === 'update')
      filePath = `${this.settingService.PORTABLE_EXECUTABLE_DIR}/updating DocumentBurster, please wait`;

    const jobFileName = Utilities.getRandomJobFileName();

    const jobFilePath = `${this.settingService.PORTABLE_EXECUTABLE_DIR}/temp/${jobFileName}`;
    const jobFileContent = Utilities.getJobFileContent(
      filePath,
      jobType,
      '14234234324324',
    );

    await UtilitiesElectron.writeAsync(jobFilePath, jobFileContent);
    return jobFilePath;
  }

  async emptyLogFile() {
    return this.fsService.writeAsync(this.logFilePath, '');
  }

  async installChocolatey(): Promise<void> {
    return this.apiService.post('/jobman/system/install/chocolatey');
  }

  async unInstallChocolatey(): Promise<void> {
    return this.apiService.post('/jobman/system/uninstall/chocolatey');
  }

  async log(message: string) {}

  async deleteJobFile(jobFilePath: string) {
    return this.fsService.removeAsync(jobFilePath);
  }

  restartElectronApp() {
    //this should call the RUST writen program rb_rust_updater
    //with the Electron APIs the restartElectronApp() never worked properly
  }
}

import { Injectable } from '@angular/core';
import { BsModalRef, BsModalService } from 'ngx-bootstrap/modal';
import { ConfirmDialogComponent } from './confirm-dialog.component';

@Injectable()
export class ConfirmService {
  modalRef?: BsModalRef;
  constructor(protected modalService: BsModalService) {}

  askConfirmation(options: any): Promise<any> {
    return new Promise((resolve, reject) => {
      this.modalRef = this.modalService.show(ConfirmDialogComponent);
      this.modalRef.content.title = options.title
        ? options.title
        : 'Confirmation';
      this.modalRef.content.message = options.message;
      this.modalRef.content.confirmLabel = options.confirmLabel
        ? options.confirmLabel
        : 'Yes';
      this.modalRef.content.declineLabel = options.declineLabel
        ? options.declineLabel
        : 'No';

      this.modalRef.content.confirmAction = options.confirmAction;

      this.modalRef.content.onClose.subscribe((result: boolean) => {
        this.modalService.hide();
        resolve(result);
      });
    });
  }
}

