@Injectable({
  providedIn: 'root',
})
export class FsService {
  constructor(protected apiService: ApiService) {}

  async removeAsync(path: string) {
    console.log('removeAsync', path);
    return this.apiService.delete(
      `/jobman/system/fs/delete-quietly?path=${encodeURIComponent(
        Utilities.slash(path),
      )}`,
    );
  }

  async copyAsync(
    sourcePath: string,
    destinationPath: string,
    criteria: {
      overwrite?:
        | boolean
        | ((src: any, dest: any) => boolean | Promise<boolean>);
      matching?: string[];
      ignoreCase?: boolean;
    } = {},
  ): Promise<void> {
    const overwrite =
      typeof criteria.overwrite === 'function'
        ? criteria.overwrite(sourcePath, destinationPath)
        : criteria.overwrite;

    console.log(`overwrite = ${overwrite}`);

    let postUrl = `/jobman/system/fs/copy?fromPath=${encodeURIComponent(
      Utilities.slash(sourcePath),
    )}&toPath=${encodeURIComponent(Utilities.slash(destinationPath))}`;

    if (overwrite) postUrl = `${postUrl}&overwrite=${overwrite}`;
    if (criteria.matching) postUrl = `${postUrl}&matching=${criteria.matching}`;
    if (criteria.matching)
      postUrl = `${postUrl}&ignoreCase=${criteria.ignoreCase}`;

    return this.apiService.post(postUrl);
  }

  async existsAsync(path: string): Promise<'dir' | 'file' | 'other' | false> {
    return this.apiService.get(
      '/jobman/system/fs/exists',
      {
        path: encodeURIComponent(Utilities.slash(path)),
      },
      new Headers({
        Accept: 'text/plain',
        'Content-Type': 'application/json',
      }),
    );
  }

  async dirAsync(
    path: string,
    criteria: {
      empty?: true;
      mode?: string;
    } = {},
  ): Promise<void> {
    return this.apiService.post(
      `/jobman/system/fs/dir?path=${encodeURIComponent(Utilities.slash(path))}`,
      criteria,
    );
  }

  async readAsync(path: string): Promise<string> {
    return this.apiService.get(
      '/jobman/system/fs/read-file-to-string',
      { path: encodeURIComponent(path) },
      new Headers({
        Accept: 'text/plain',
        'Content-Type': 'application/json',
      }),
    );
  }

  async writeAsync(path: string, content: string) {
    const encodedPath = encodeURIComponent(Utilities.slash(path));
    return this.apiService.post(
      `/jobman/system/fs/write-string-to-file?path=${encodedPath}`,
      content,
      new Headers({
        'Content-Type': 'text/plain',
      }),
    );
  }

  async fileAsync(
    path: string,
    criteria: {
      content?: string | Buffer | Object | Array<any>;
      jsonIndent?: number;
      mode?: number | string;
    } = {},
  ): Promise<void> {
    const encodedPath = encodeURIComponent(Utilities.slash(path));

    return this.apiService.post(
      `/jobman/system/fs/file?path=${encodedPath}`,
      criteria,
    );
  }

  inspectAsync(
    path: string,
    criteria: {
      checksum?: 'md5' | 'sha1' | 'sha256' | 'sha512';
      mode?: boolean;
      times?: boolean;
      absolutePath?: boolean;
      symlinks?: 'report' | 'follow';
    } = {},
  ): Promise<InspectResult | undefined> {
    return this.apiService.get(
      `/jobman/system/fs/inspect/${encodeURIComponent(Utilities.slash(path))}`,
      {
        checksum: criteria.checksum,
        mode: criteria.mode,
        times: criteria.times,
        absolutePath: criteria.absolutePath,
        symlinks: criteria.symlinks,
      },
    );
  }

  async resolveAsync(path: string): Promise<string> {
    console.log(`path = ${path}`);
    return this.apiService.get(
      '/jobman/system/fs/resolve',
      {
        path: encodeURIComponent(Utilities.slash(path)),
      },
      new Headers({
        Accept: 'text/plain',
        'Content-Type': 'application/json',
      }),
    );
  }
}

import { Injectable } from '@angular/core';
import { BsModalRef, BsModalService } from 'ngx-bootstrap/modal';
import { InfoDialogComponent } from './info-dialog.component';
@Injectable()
export class InfoService {
  modalRef?: BsModalRef;
  constructor(protected modalService: BsModalService) {}

  showInformation(options: any): Promise<any> {
    return new Promise((resolve, reject) => {
      this.modalRef = this.modalService.show(InfoDialogComponent);
      this.modalRef.content.title = options.title
        ? options.title
        : 'Information';
      this.modalRef.content.message = options.message;
      this.modalRef.content.confirmLabel = options.confirmLabel
        ? options.confirmLabel
        : 'OK';

      this.modalRef.content.onClose.subscribe((result: boolean) => {
        resolve(result);
      });
    });
  }
}

import { Injectable } from '@angular/core';

import * as xml2js from 'xml2js';

import { unserialize } from 'php-serialize';

import { Changelog, parser } from 'keep-a-changelog';

import * as semver from 'semver';

import Utilities from '../helpers/utilities';
import { SettingsService } from './settings.service';
import { ShellService } from './shell.service';
import { FsService } from './fs.service';

@Injectable({
  providedIn: 'root',
})
export class LicenseService {
  protected licenseFilePath: string;

  licenseDetails: any;

  changeLogStr: string;
  changeLog: Changelog;

  latestVersion: semver.SemVer;
  isNewerVersionAvailable: boolean = false;

  constructor(
    protected settingsService: SettingsService,
    protected shellService: ShellService,
    protected fsService: FsService,
  ) {
    this.licenseFilePath = Utilities.slash(
      `${this.settingsService.CONFIGURATION_FOLDER_PATH}/_internal/license.xml`,
    );
  }

  async saveLicenseFileAsync() {
    console.log(
      `saveLicenseFileAsync - this.licenseDetails = ${JSON.stringify(
        this.licenseDetails,
      )}`,
    );

    const builder = new xml2js.Builder();

    const licenseDetailsAsString = builder.buildObject(this.licenseDetails);

    //console.log(
    //  `saveLicenseFileAsync - licenseDetailsAsString = ${licenseDetailsAsString}`
    //);

    return this.fsService.writeAsync(
      this.licenseFilePath,
      licenseDetailsAsString,
    );
  }

  async loadLicenseFileAsync() {
    //if (this.latestVersion) return;

    //console.log(`this.latestVersion = ${this.latestVersion}`);

    //console.log(`license.service.loadLicenseFileAsync()`);

    const content = await this.fsService.readAsync(this.licenseFilePath);

    //console.log(`license.service.content = ${content}`);

    this.licenseDetails = await Utilities.parseStringPromise(content, {
      trim: true,
      explicitArray: false,
    });

    // console.log(
    //   `loadLicenseFileAsync - this.licenseDetails = ${JSON.stringify(
    //     this.licenseDetails
    //   )}`
    // );

    if (this.licenseDetails.license.latestversion) {
      this.latestVersion = this.licenseDetails.license.latestversion;
      this.changeLogStr = this.licenseDetails.license.changelog;
    } // if it is a demo installation
    else {
      const response = await this.getChangeLogForTheDemoInstallationToo();
      const bodyAsJson = await response.json();

      //console.log(`response.json: ${JSON.stringify(bodyAsJson)}`);

      this.latestVersion = bodyAsJson.new_version;

      const { changelog } = unserialize(bodyAsJson.sections, {
        description: String,
        changelog: String,
      });
      this.changeLogStr = changelog;
    }

    this.isNewerVersionAvailable = false;

    if (!this.settingsService.version)
      await this.settingsService.loadDefaultSettingsFileAsync();

    this.latestVersion = semver.coerce(this.latestVersion);
    this.settingsService.version = semver.coerce(this.settingsService.version);

    //console.log(
    //  `this.latestVersion = ${this.latestVersion}, this.settingsService.version = ${this.settingsService.version}`
    //);

    if (semver.gt(this.latestVersion, this.settingsService.version)) {
      this.isNewerVersionAvailable = true;
    }

    try {
      //the "Improved software changelog" keepachangelog format was implemented in
      //DocumentBurster v8.8.0
      this.changeLog = parser(this.changeLogStr);
    } catch {}
  }

  getChangeLogForTheDemoInstallationToo(): Promise<any> {
    return Utilities.httpGet(
      `https://www.pdfburst.com/store?edd_action=get_version&item_name=${this.settingsService.product}`,
    );
  }

  verifyLicense(action, exitCallback?): Promise<void> {
    return this.shellService.runBatFile([action], 'license', exitCallback);
  }

  deActivateLicense(exitCallback?) {
    this.shellService.runBatFile(['-dl'], 'license', exitCallback);
  }
}

