          );
    }
    scriptContent = scriptContent.replace(
      new RegExp('\\$hostName', 'g'),
      '${hostName}',
    );

    if (scriptContent.includes('$hostName'))
      if (this.log)
        this.log.error(
          `migrateScriptFile - ${newScriptFilePath} '$hostName' not expected!`,
        );

    scriptContent = scriptContent.replace(
      new RegExp('\\$tempFilePath', 'g'),
