    if (scriptContent.includes('$sharedLocationPath'))
      if (this.log)
        this.log.error(
          `migrateScriptFile - ${newScriptFilePath} '$sharedLocationPath' not expected!`,
        );

    scriptContent = scriptContent.replace(
      new RegExp('\\$uploadFilePath', 'g'),
