})
export class ButtonClearLogsComponent {
  @Input() id: string;
  @Input() value: string;
  @Input() title: string;
  @Input() question: string;

  constructor(
    protected logsService: LogsServiceWebSocket,
    protected executionStatsService: ExecutionStatsService,
    protected confirmService: ConfirmService,
