  onUseExistingEmailConnectionClick(event: Event) {
    if (event instanceof Event)
      if ((event.target as HTMLInputElement).checked)
        this.fillExistingEmailConnectionDetails(
          this.selectedEmailConnectionFile.connectionCode,
        );
