  `,
})
export class InfoDialogComponent implements OnInit {
  onClose: Subject<boolean>;
  title: string;
  message: string;
  confirmLabel: string;

  constructor(protected bsModalRef: BsModalRef) {}

  ngOnInit(): void {
    this.onClose = new Subject();
  }

  confirm() {
    this.onClose?.next(true);
    this.bsModalRef.hide();
  }
}

